import json
import sys

# Helper to extract ID from value (handles plain ID, int, or JSON object)
def get_id(val):
    if not val:
        return 0
    try:
        obj = json.loads(str(val))
        if isinstance(obj, dict):
            return int(obj.get('id', 0))
        return int(obj)
    except (json.JSONDecodeError, TypeError, ValueError):
        return int(val)

device_body = {
    'name': sys.argv[1],
    'tenant': get_id(sys.argv[2]),
    'site': get_id(sys.argv[3]),
    'device_type': get_id(sys.argv[4]),
    'role': get_id(sys.argv[5]),
    'serial': sys.argv[6],
    'status': 'planned',
    'custom_fields': {'ccc_template_name': 'Ansible_Day0-Template', 'ccc_pid': sys.argv[7]}
}
device_json = json.dumps(device_body)
