import json
import sys

raw_response = sys.argv[1]
tenant_name = sys.argv[2]
site_name = sys.argv[3]
location_name = sys.argv[4]

# Parse site response - could be single object or wrapped in results
try:
    response = json.loads(raw_response) if raw_response else {}
    # Single object endpoint returns object directly
    # List endpoint returns {count, results}
    if 'results' in response:
        site = response['results'][0] if response['results'] else {}
    else:
        site = response
except:
    site = {}

# Get region from site details
region = site.get('region', {}) or {}
region_name = region.get('name', '') if region else ''

# Build site path - filter out empty segments
path_parts = ['Global', tenant_name, region_name, site_name, location_name]
path_parts = [p for p in path_parts if p]  # Remove empty strings
site_path = '/'.join(path_parts)

print(f"Site path: {site_path}, Region: {region_name}")
